package curs3;

public class IfElseExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 5;
		
		if (number > 0 ) {   //conditie
				System.out.println("Pozitiv");	//se executa daca conditia este adevarata
		} else {		
			System.out.println("Negativ"); //se executa daca conditia este falsa
		}

	}

}
