package curs5;

public class WhileExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i < 10; i++) {
			System.out.println("Valoarea lui i este: " + i);
		}

		System.out.println("=================================");

		int j = 0;
		while (j < 10) {
			System.out.println("Valoarea lui j este: " + j);
			j++;
		}

	}

}
